-- VIEW 종속성 오류를 극복하기 위해 임시 테이블을 생성합니다.
CREATE TABLE `search_v` (
	`LIB_BOOK_CD` INT(11) NOT NULL COMMENT '소장도서코드',
	`ISBN` CHAR(15) NOT NULL COMMENT 'ISBN' COLLATE 'utf8mb4_general_ci',
	`TITLE` VARCHAR(255) NULL COMMENT '제목' COLLATE 'utf8mb4_general_ci',
	`AUTHOR_NM` VARCHAR(50) NULL COMMENT '이름' COLLATE 'utf8mb4_general_ci',
	`RENT_YN` TINYINT(4) NULL COMMENT '대출여부',
	`GENRE` MEDIUMTEXT NULL COLLATE 'utf8mb4_general_ci',
	`B_IMG` VARCHAR(500) NULL COMMENT '이미지' COLLATE 'utf8mb4_general_ci',
	`B_INTRO` VARCHAR(10000) NULL COMMENT '소개글' COLLATE 'utf8mb4_general_ci'
) ENGINE=MyISAM;

