-- 뷰 library_chatbot.user_rent_list 구조 내보내기
-- VIEW 종속성 오류를 극복하기 위해 임시 테이블을 생성합니다.
CREATE TABLE `user_rent_list` (
	`RENT_CD` INT(11) NOT NULL COMMENT '대출내역코드',
	`도서명` VARCHAR(255) NULL COMMENT '제목' COLLATE 'utf8mb4_general_ci',
	`대여일` DATE NULL COMMENT '대출일자',
	`반납일` DATE NULL COMMENT '반납일자',
	`반납상태` VARCHAR(6) NULL COLLATE 'utf8mb4_general_ci',
	`연체일자` INT(8) NULL
) ENGINE=MyISAM;

-- 뷰 library_chatbot.search_v 구조 내보내기
-- 임시 테이블을 제거하고 최종 VIEW 구조를 생성
DROP TABLE IF EXISTS `search_v`;
CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW `search_v` AS SELECT lib.LIB_BOOK_CD, lib.ISBN,  b.TITLE, a.AUTHOR_NM, lib.RENT_YN, GROUP_CONCAT(distinct g.GENRE_NM SEPARATOR ',') AS GENRE, b.B_IMG, b.B_INTRO
FROM lib_book AS lib
	JOIN book AS b
		ON lib.ISBN = b.ISBN
	JOIN b_author AS ba
		ON lib.ISBN = ba.ISBN
	JOIN author AS a
		ON ba.AUTHOR_CD = a.AUTHOR_CD
	JOIN b_genre AS bg
		ON lib.ISBN = bg.ISBN
	JOIN genre AS g
		ON bg.GENRE_CD = g.GENRE_CD
GROUP BY lib.LIB_BOOK_CD ;

-- 뷰 library_chatbot.search_view 구조 내보내기
-- 임시 테이블을 제거하고 최종 VIEW 구조를 생성
DROP TABLE IF EXISTS `search_view`;
CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW `search_view` AS SELECT lib.LIB_BOOK_CD, lib.ISBN,  b.TITLE, a.AUTHOR_NM, lib.RENT_YN, GROUP_CONCAT(distinct g.GENRE_NM SEPARATOR ',') AS GENRE, b.B_IMG, b.B_INTRO
FROM lib_book AS lib
	JOIN book AS b
		ON lib.ISBN = b.ISBN
	JOIN b_author AS ba
		ON lib.ISBN = ba.ISBN
	JOIN author AS a
		ON ba.AUTHOR_CD = a.AUTHOR_CD
	JOIN b_genre AS bg
		ON lib.ISBN = bg.ISBN
	JOIN genre AS g
		ON bg.GENRE_CD = g.GENRE_CD
GROUP BY lib.LIB_BOOK_CD ;

-- 뷰 library_chatbot.user_rent_list 구조 내보내기
-- 임시 테이블을 제거하고 최종 VIEW 구조를 생성
DROP TABLE IF EXISTS `user_rent_list`;
CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW `user_rent_list` AS SELECT RL.RENT_CD, B.TITLE AS 도서명, RL.RENT_DT AS 대여일, RL.RETURN_DT AS 반납일,
	CASE 
		WHEN DATEDIFF(RL.RETURN_DT, RL.RENT_DT) < 15 THEN '반납완료'
		WHEN RL.RETURN_DT IS NULL THEN '미반납'
		ELSE '반납기간늦음' END AS 반납상태,
	CASE 
		WHEN DATEDIFF(RL.RETURN_DT, RL.RENT_DT) < 15 THEN 0
		WHEN DATEDIFF(RL.RETURN_DT, RL.RENT_DT) THEN DATEDIFF(RL.RETURN_DT, RL.RENT_DT)-14
		ELSE DATEDIFF(CURDATE(), DATE_ADD(RL.RENT_DT, INTERVAL 14 DAY))
		 END AS 연체일자	
FROM RENT AS RL
NATURAL JOIN lib_book AS LIB
JOIN book AS B
ON LIB.ISBN = B.ISBN ;

